import React, { Component } from 'react';
import './App.css';

const isSearched = (searchTerm) => (item) =>
	    !searchTerm || item.title.toLowerCase().includes(searchTerm.toLowerCase());

class App extends Component {
	    constructor(props) {
		            super(props);
		            this.state = { list, searchTerm: '', };
		            this.onSearchChange = this.onSearchChange.bind(this); this.onDismiss = this.onDismiss.bind(this);
		        }
}

class Table extends Component { render() { const { list, pattern, onDismiss } = this.props; return (<div> {list.filter(isSearched(pattern)).map(item => <div key={item.objectID}> <span> <a href={item.url}>{item.title}</a> </span> <span>{item.author}</span> <span>{item.num_comments}</span> <span>{item.points}</span> <span> <button onClick={() => onDismiss(item.objectID)} type="button" > Dismiss </button> </span> </div>)} </div>); } }

class Search extends Component {
	    render() {
		            const { value, onChange } = this.props;
		            return (
				                <form>
				                    <input type="text" value={value} onChange={onChange} />
				                </form>
				            );
		        }
}

export default App;
