import "./App.css";
import image1 from "./Peanut-Butter-Banana-Smoothie.jpg";
import React, { Component } from "react";

// Declare our two recipes in JSON format.
const recipes = [
  {
    image: image1,
    title: "Banana Peanut Smoothie",
    summary: "Yummy, easy, and smooth!",
    steps: "1. Hello 2. World",
    ingredients:
      "1 ripe banana, 1 Tbs of peanut butter, 3/4 cup of milk, 1 ice cube"
  },
  {
    image: image1,
    title: "Healthy Cookies",
    summary: "Hearty & healthy",
    steps: "1. Get peanut butter. 2. Whole wheat flour. 3. Eggs.",
    ingredients:
      "2 cups whole oats, 1.5 cups whole wheat flour, 2 Tbs of peanut butter, 2 eggs"
  }
];

/*
const imageClick = () => {
  console.log("clicked : "); // works
};
*/

// imageClick: called when a recipe picture, title, or summary is clicked
// This function first removes all elements on the homepage, then it displays the details of the recipe
// that was clicked.
function imageClick(title, index) {
  const myNode = document.getElementById("page");
  while (myNode.firstChild) {
    myNode.removeChild(myNode.firstChild);
  }
  return (
    <div key={index}>
      <h4>Ingredients: {recipes[index].ingredients}</h4>
    </div>
  );
}

class Recipe extends React.Component {
  render() {
    return (
      <div className="table">
        <img src={recipes.image} onClick={this.imageClick} />
        <h3> {recipes.title} </h3>
      </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { recipes };

    this.imageClick = this.imageClick.bind(this);
  }

  // imageClick: called when a recipe picture, title, or summary is clicked
  // Will take user to page that displays the details of the recipe.
  imageClick = (title, index) => {
    const myNode = document.getElementById("page");
    console.log(myNode);
    console.log("title : " + title);

    sessionStorage.setItem("index", index);
    /*  Below attempt at appending HTML doesn't work :(
    const div = document.createElement("recipe");
    div.className = "recipeClicked";
    div.innerHTML = <h1>Ingredients xyz: title</h1>;
    document.getElementById('page').appendChild(div);
    */
  };

  render() {
    return (
      <div id="page0">
        <a href="Page2.js" rel="nofollow">
          Page 2
        </a>
        <div id="page">
          <div className="interactions">
            <Homepage recipes={recipes} onClick={this.imageClick} />
          </div>
        </div>
      </div>
    );
  }
}

const Homepage = ({ recipes, onClick }) => (
  <div>
    <ul>
      {recipes.map((item, index) => (
        <div key={item.title}>
          <a
            src="Page2.js"
            type="button"
            className="card-link"
            onClick={() => imageClick(item.title, index)}
          >
            <span>
              <img src={item.image} />
            </span>
            <span>
              <h2 id={item.title}>{item.title}</h2>
            </span>
            <span>
              <h4>{item.summary}</h4>
            </span>
            <br />
            <br />
          </a>
        </div>
      ))}
    </ul>
  </div>
);

export default App;
